import React, { useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  // const dummyMovies = [
  //   {
  //     id: 1,
  //     title: 'Some Dummy Movie',
  //     openingText: 'This is the opening text of the movie',
  //     releaseDate: '2021-05-18',
  //   },
  //   {
  //     id: 2,
  //     title: 'Some Dummy Movie 2',
  //     openingText: 'This is the second opening text of the movie',
  //     releaseDate: '2021-05-19',
  //   },
  // ];
  const [movieList, setMovieList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);

  const fetchMoviesHandler = () => {
    console.log("Fetching movies ...");
    setLoading(true);
    setError(false);
    fetch("https://swapi.dev/api/film")
      .then((response) => {
        console.log("Received response ...");
        if (!response.ok) {
          throw Error("Failed to fetch movies");
        }
        return response.json();
      })
      .then((data) => {
        let id = 1;
        console.log("Res", data);
        if (data.results.length > 0) {
          const movies = data.results.map((movie) => {
            return {
              id: id++,
              title: movie.title,
              openingText: movie.opening_crawl,
              releaseDate: movie.release_date,
            };
          });
          setError(false);
          setLoading(false);
          setMovieList(movies);
        }
      })
      .catch(() => {
        console.log("Something went wrong");
        setError(true);
        setLoading(false);
      });
  };

  // const content = () => {
  //   if (loading) {
  //     return <p>Loading ...</p>;
  //   } else if (error) {
  //     return <p>Something went wrong</p>;
  //   } else if (movieList.length > 0) {
  //     return <MoviesList movies={movieList} />;
  //   } else if (movieList.length === 0) {
  //     return <p>No movie found</p>;
  //   }
  //   return <p>.....</p>;
  // };

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section>
      <section>
        {loading && <p>Loading ...</p>}
        {!loading && error && <p>Something went wrong</p>}
        {!loading && !error && movieList.length > 0 && (
          <MoviesList movies={movieList} />
        )}
        {!loading && !error && movieList.length === 0 && <p>No movies found</p>}
      </section>
    </React.Fragment>
  );
}

export default App;
